import React, {useState} from "react";
import { ScrollView, FlatList } from "react-native";
import CotacaoDiaria from "../CardCotacaoDiaria/index";
import styles from "./styles";
import { useFetch } from "../../hooks/useFetch";


//insert zero to the left
function addZero(number) {
  if (number <= 9) {
    return "0" + number;
  }
  return number;
}

//Date formatting to get API
function url(qtdDias) {
  const date = new Date();
  const listLastDays = qtdDias;
  const end_date = 
    `${date.getFullYear()}
    -${addZero(date.getMonth() + 1)}
    -${addZero(date.getDate())}`;
  date.setDate(date.getDate() - listLastDays);
  const start_date = 
    `${date.getFullYear()}
    -${addZero(date.getMonth() + 1)}
    -${addZero(date.getDate())}`;

  //URL  GET API
  const urlSearchCoins = `https://api.coindesk.com/v1/bpi/historical/close.json?start=${start_date}&end=${end_date}`;
  return urlSearchCoins;
}
function buscainfo(diasquery) {
  const { data } = useFetch(url(diasquery));
  if (data) {
    const queryCoinsList = Object.keys(data).map((key) => {
      return {
        data: key,
        valor: data[key],
      };
    });

    return queryCoinsList;
  }
}


export default function CardCotacoes() {



  const coinsTrans = buscainfo(5);

  return (
    <ScrollView style={styles.headerCard}>

      <FlatList
        data={coinsTrans}
        renderItem={({ item }) => {
          return <CotacaoDiaria valor={item.valor} data={item.data} />;
        }}
      />

    </ScrollView>

  );
}
