import React, { Fragment } from "react";
import { ScrollView, View, FlatList, Text } from "react-native";
import CotacaoDiaria from "../CardCotacaoDiaria/index";

import styles from "./styles";



export default function CardCotacoes(props) {
  const daysQuery = props.filterDay
  return (
    <Fragment>
      <View style={styles.filtro}>
        <Text style={styles.x} onPress={() => daysQuery(1)}>
          1D
        </Text>
        <Text style={styles.x} onPress={() => daysQuery(7)}>
          7D
        </Text>
        <Text style={styles.x} onPress={() => daysQuery(30)}>
          1M
        </Text>
        <Text style={styles.x} onPress={() => daysQuery(180)}>
          6M
        </Text>
      </View>
      <ScrollView style={styles.headerCard}>
        <View style={{ marginTop: 40 }}>
          <FlatList
            data={props.listTransactions}
            renderItem={({ item }) => {
              return <CotacaoDiaria valor={item.valor} data={item.data} />;
            }}
          />
        </View>
      </ScrollView>
    </Fragment>
  );
}
