import React, { useState, useEffect, Fragment } from "react";
import { ScrollView, View, FlatList, Text } from "react-native";
import CotacaoDiaria from "../CardCotacaoDiaria/index";
import Grafico from '../Grafico/';
import styles from "./styles";

//insert zero to the left
function addZero(number) {
  if (number <= 9) {
    return "0" + number;
  }
  return number;
}

//Date formatting to get API
function url(qtdDias) {
  const date = new Date();
  const listLastDays = qtdDias;
  const end_date = `${date.getFullYear()}-${addZero(
    date.getMonth() + 1
  )}-${addZero(date.getDate())}`;
  date.setDate(date.getDate() - listLastDays);
  const start_date = `${date.getFullYear()}-${addZero(
    date.getMonth() + 1
  )}-${addZero(date.getDate())}`;

  //URL  GET API
  const urlSearchCoins = `https://api.coindesk.com/v1/bpi/historical/close.json?start=${start_date}&end=${end_date}`;
  return urlSearchCoins;
}

async function getListCoins(url) {
  let response = await fetch(url);
  let joao = await response.json();

  let dados = joao.bpi;
  const queryCoinsList = Object.keys(dados).map((key) => {
    return {
      data: (key).split('-').reverse().join('/'),
      valor: dados[key],
    };
  });
  let data = queryCoinsList.reverse();
  return data;
}

async function getPriceCoins(url) {
  let responseG = await fetch(url);
  let joaoG = await responseG.json();
  let dadosG = joaoG.bpi;
  const queryCoinsListG = Object.keys(dadosG).map((key) => {
    return dadosG[key]
  });
  let dataG = queryCoinsListG;
  return dataG;

}



export default function CardCotacoes() {
  const [coinsList, setcoinsList] = useState([]);
  const [coinsGrafichList, setcoinsGrafichList] = useState([]);
  const [days, setdays] = useState(2);

  function e(number) {
    setdays(number);
  }

  useEffect(() => {
    getListCoins(url(days)).then((data) => {
      setcoinsList(data);
    });
    getPriceCoins(url(days)).then((dataG) => {
      setcoinsGrafichList(dataG);

    });
    console.log(coinsGrafichList)
  }, [days]);

  return (

    <Fragment>
       <Grafico/>
      <View style={styles.filtro}>
        <Text 
        style={styles.x} 
        onPress={() => e(1)}>
          1D
        </Text>
        <Text 
        style={styles.x} 
        onPress={() => 
        e(7)}>
          7D
        </Text>
        <Text 
        style={styles.x} 
        onPress={() => e(30)}>
          1M
        </Text>
        <Text 
        style={styles.x} 
        onPress={() => e(180)}>
          6M
        </Text>
      </View>
      <ScrollView style={styles.headerCard}>
        <View style={{ marginTop: 40 }}>
          <FlatList
            data={coinsList}
            renderItem={({ item }) => {
              return <CotacaoDiaria valor={item.valor} data={item.data} />;
            }}
          />
        </View>
      </ScrollView>
    </Fragment>
  );
}
