import React, {useState, useEffect} from "react";
import { ScrollView, FlatList } from "react-native";
import CotacaoDiaria from "../CardCotacaoDiaria/index";
import styles from "./styles";


//insert zero to the left
function addZero(number) {
  if (number <= 9) {
    return "0" + number;
  }
  return number;
}

//Date formatting to get API
function url(qtdDias) {
  const date = new Date();
  const listLastDays = qtdDias;
  const end_date = `${date.getFullYear()}-${addZero(date.getMonth() + 1)}-${addZero(date.getDate())}`;
  date.setDate(date.getDate() - listLastDays);
  const start_date = `${date.getFullYear()}-${addZero(date.getMonth() + 1)}-${addZero(date.getDate())}`;

  //URL  GET API
  const urlSearchCoins = `https://api.coindesk.com/v1/bpi/historical/close.json?start=${start_date}&end=${end_date}`;
  return urlSearchCoins;
}

let datalist = url(10)
console.log(datalist)
async function getListCoins(){
  let response = await fetch(datalist)
  let data =  await response.json()
  console.log('funcionou')
let dados = data.bpi
  const queryCoinsList = Object.keys(dados).map((key) => {
    return {
      data: key,
      valor: dados[key],
    };
  });
return queryCoinsList


}




export default function CardCotacoes() {

  const [coinsList, setcoinsList] = useState([])

  useEffect(() => {

    getListCoins().then (data =>{
      setcoinsList(data)

     })
  }, [])




  return (
    <ScrollView style={styles.headerCard}>

      <FlatList
        data={
         [{valor:'3562.330', data:'25/05/2021'}]
        }
        renderItem={({ item }) => {
          return <CotacaoDiaria valor={item.valor} data={item.data} />;
        }}
      />

    </ScrollView>

  );
}
