import React from "react";
import { View, Text, Image } from "react-native";
import styles from "./style";
import { useFetch } from '../../hooks/useFetch';


//insert zero to the left
function addZero(number){
  if (number <= 9) {
    return "0" + number;
  }
    return number; 
}


const date = new Date();
const listLastDays = 10

//Date formatting to get API
const end_date = `${date.getFullYear()}-${addZero(date.getMonth()+1)}-${addZero(date.getDate())}`
date.setDate(date.getDate() - listLastDays)
const start_date = `${date.getFullYear()}-${addZero(date.getMonth()+1)}-${addZero(date.getDate())}`

//URL  GET API
const urlBase = `https://api.coindesk.com/v1/bpi/historical/close.json?`
const param = `start=${start_date}&end=${end_date}`
const getApiBitcoin = urlBase+param

export default function CardCotacaoDiaria() {

  const {data} = useFetch(`${urlBase+param}`)


  if(!data){
     return (

      <View style={styles.cardPrincipal}>
      <View style={styles.contextLeft}>
        <View style={styles.boxLogo}>
          <Image
            style={styles.logBitcoin}
            source={require("../../images/logoBitcoin-removebg-preview.png")}
          />
          <Text style={styles.nameCripto}>Bitcoin</Text>
        </View>
        <Text style={styles.priceDate}>16 de mar 2021</Text>
      </View>
      <View style={styles.contextRigth}>
        <Text style={styles.price}>R$ 310.605,78</Text>
      </View>
    </View>
  );
   }


  return (
    <View style={styles.cardPrincipal}>
      <View style={styles.contextLeft}>
        <View style={styles.boxLogo}>
          <Image
            style={styles.logBitcoin}
            source={require("../../images/logoBitcoin-removebg-preview.png")}
          />
          <Text style={styles.nameCripto}>Bitcoin</Text>
        </View>
        <Text style={styles.priceDate}>16 de mar 2021</Text>
      </View>
      <View style={styles.contextRigth}>
        <Text style={styles.price}>R$ 310.605,78</Text>
      </View>
    </View>
  );
}
